import { useState } from "react";

const Todo = () => {
  const todosList = [
    {
      id: 1,
      content: "店予約する",
      toggle: true,
    },
    {
      id: 2,
      content: "卵買う",
      toggle: true,
    },
    {
      id: 3,
      content: "郵便出す",
      toggle: true,
    },
  ];

  const [list, setList] = useState(todosList);

  const toggleChange = (oldtaskid) => {
    const newLists = todosList.map((task) => {
      const newtask = { ...task };
      if (newtask.id === oldtaskid) {
        newtask.toggle = !newtask.toggle;
      }

      return newtask;
    });
    setList(newLists);
    setList((prevstate) => {
      return setList(prevstate);
    });
  };

  return (
    <>
      {list.map((task) => {
        if (task.toggle === true) {
          return (
            <div>
              <button key={task.id} onClick={() => toggleChange(task.id)}>
                完了
              </button>
              <div>{task.content}</div>
            </div>
          );
        }
      })}
    </>
  );
};

export default Todo;
