import { useState } from "react";

const Todo = () => {
  const todosList = [
    {
      id: 1,
      content: "店予約する",
      toggle: true,
    },
    {
      id: 2,
      content: "卵買う",
      toggle: true,
    },
    {
      id: 3,
      content: "郵便出す",
      toggle: true,
    },
  ];

  const toggleChange = (task) => {
    task.toggle = !task.toggle;
  };

  const Remaintasks = todosList.map((task) => {
    if (task.toggle === true) {
      return (
        <div>
          <button key={task.id} onClick={toggleChange(task)}>
            完了
          </button>
          <div>{task.content}</div>
        </div>
      );
    }
  });

  return <>{Remaintasks}</>;
};

export default Todo;
