import { useState } from "react";

const Todo = () => {
  const todosList = [
    {
      id: 1,
      content: "店予約する",
    },
    {
      id: 2,
      content: "卵買う",
    },
    {
      id: 3,
      content: "郵便出す",
    },
  ];

  const [list, setList] = useState(todosList);

  const deleteTask = (oldtaskid) => {
    const newLists = todosList.filter((task) => {
      return task.id !== oldtaskid;
    });
    setList(newLists);
  };

  return (
    <>
      {list.map((task) => {
        return (
          <div key={task.id}>
            <button onClick={() => deleteTask(task.id)}>完了</button>
            <div>{task.content}</div>
          </div>
        );
      })}
    </>
  );
};

export default Todo;
