import { useState } from "react";

const Example = () => {
  const [ischecked, checkToggle] = useState(true);
  const toggle = (e) => {
    checkToggle((prevState) => {
      let state = !prevState;
      return state;
    });
  };
  return (
    <>
      <label>
        <input
          type="checkbox"
          checked={ischecked}
          onchange={(e) => toggle(e)}
        />
      </label>
    </>
  );
};

export default Example;
