import { useState } from "react";

const Todo = () => {
  const todosList = [
    {
      id: 1,
      content: "店予約する",
      toggle: true,
    },
    {
      id: 2,
      content: "卵買う",
      toggle: true,
    },
    {
      id: 3,
      content: "郵便出す",
      toggle: true,
    },
  ];

  const [list, setList] = useState();

  const toggleChange = () => {
    const newLists = todosList.map((task) => {
      const newtask = { ...task };
      newtask.toggle = !newtask.toggle;
      return newtask;
    });
    setList(newLists);
  };

  return (
    <>
      {todosList.map((task) => {
        if (task.toggle === true) {
          return (
            <div>
              <button key={task.id} onClick={toggleChange}>
                完了
              </button>
              <div>{task.content}</div>
            </div>
          );
        }
      })}
    </>
  );
};

export default Todo;
