import { useState } from "react";

const Example = () => {
  const [val, setVal] = useState();
  return (
    <div>
      <label htmlFor="123">ラベル</label>
      <div>
        <input
          id="123"
          placeholder="こんにちは"
          valur={val}
          onChange={(e) => setVal(e.target.value)}
        ></input>
      </div>
    </div>
  );
};

export default Example;
