import { StackDivider, VStack, HStack } from "@chakra-ui/react";

const List = ({ todos, deleteTodo }) => {
  const complete = (id) => {
    deleteTodo(id);
  };
  return (
    <VStack
      divider={<StackDivider />}
      borderColor="black.100"
      borderWidth="1px"
      borderRadius="3px"
      p={5}
      alignItems="start"
    >
      {todos.map((todo) => {
        return (
          <HStack key={todo.id}>
            <IconButton onClick={() => complete(todo.id)}>完了</IconButton>
            <span>{todo.content}</span>
          </HStack>
        );
      })}
    </VStack>
  );
};

export default List;
